UPDATE public.film SET name = 'Меч Короля Артура', duration = 126, rating = 7.4, poster = 'https://st.kp.yandex.net/images/film_iphone/iphone360_824437.jpg', is3d = true, description = 'Молодой Артур живёт на задворках Лондиниума вместе со своей бандой. Он и понятия не имел о своём королевском происхождении, пока однажды не взял в руки меч Эскалибур. Меч начинает менять Артура. В результате он присоединяется к сопротивлению и таинственной молодой девушке по имени Гвиневра. Ему предстоит научиться понимать магическое оружие, которым он овладел, столкнуться со своими собственными демонами и объединить народ в борьбе против диктатора Вортигерна, убившего его родителей и завладевшего короной.', trailer = 'https://youtu.be/KCgtQUb03GA' WHERE id = 3;
