UPDATE public.film SET name = 'Стражи галктики 2', duration = 136, rating = 7.7, poster = 'https://www.kinopoisk.ru/images/film_big/841263.jpg', is3d = true, description = 'Все в сборе: землянин Питер Квилл (Звездный Лорд), молчаливый громила Дракс, зеленокожая наемница Гамора, живое дерево Грут и говорящий енот. Герои не изменяют себе и с завидной регулярностью продолжают попадать в немыслимые ситуации, выпутываясь из них почти без ущерба (а иногда даже с пользой) для окружающих. На этот раз им предстоит раскрыть одну из самых главных тайн во всей Галактике: кто же на самом деле отец Питера Квилла?', trailer = 'https://youtu.be/w5siIauNEw4' WHERE id = 7;